function createDropDown(dropdown, target) {
    $("#" + target).remove();
    var source = $(dropdown);
    var selected = source.find("option[selected]");
    if (selected == null || selected.length == 0) {
        source.prop("selectedIndex", 0);
        source.find('option:first').attr("selected", "selected");
        selected = source.find("option[selected]");
    }
    //var options = $("option", source);
    var opts = $(dropdown + " optgroup");
    var ts = $.trim(selected.text());
    if (ts.length > 15) ts = ts.substring(0, 15);
    $(dropdown).after('<dl id="' + target + '" class="dropdown"></dl>');
    $("#" + target).append('<dt><a href="#">' + ts + '</a></dt>');
    $("#" + target).append('<dd><ul></ul></dd>');

    var co = false;
    opts.each(function() {
        co = true;
        $("#" + target + " dd ul").append('<li class="group-header">' +
            $(this).attr('label') + '</li>');
        var options;
        options = $("option", this);
        options.each(function() {
            $("#" + target + " dd ul").append('<li><a href="#">' +
                $(this).text() + '<span class="value">' +
                $(this).val() + '</span></a></li>');
        });
    });

    if (co == false) {
        var options = $("option", source);
        options.each(function() {
            $("#" + target + " dd ul").append('<li><a href="#">' +
                $(this).text() + '<span class="value">' +
                $(this).val() + '</span></a></li>');
        });
    }

    $(dropdown).hide();

    $("#" + target + " dt a").click(function(e) {
        e.preventDefault();

        if (target == 'target2') {
            $("#target1 dd ul").hide();
            $("#target3 dd ul").hide();
            $("#target4 dd ul").hide();
        }
        if (target == 'target1') {
            $("#target2 dd ul").hide();
            $("#target3 dd ul").hide();
            $("#target4 dd ul").hide();
        }
        if (target == 'target3') {
            $("#target1 dd ul").hide();
            $("#target2 dd ul").hide();
            $("#target4 dd ul").hide();
        }
        if (target == 'target4') {
            $("#target2 dd ul").hide();
            $("#target3 dd ul").hide();
            $("#target1 dd ul").hide();
        }

        $("#" + target + " dd ul").toggle();

        return false;
    });

    $(document).bind('click', function(e) {
        var $clicked = $(e.target);
        if (!$clicked.parents().hasClass("dropdown"))
            $("#" + target + " dd ul").hide();
    });

    $("#" + target + " dd ul li a").click(function(e) {
        e.preventDefault();
        var text = $(this).html();
        text = removeElements(text, 'span');
        $("#" + target + " dt a").html(text);
        $("#" + target + " dd ul").hide();

        var source = $(dropdown);
        source.val($(this).find("span.value").html());

        if (dropdown == '#Loc') {
            updateHotel();
        }
        return false;
    });
}

var removeElements = function(text, selector) {
    var wrapped = $("<div>" + text + "</div>");
    wrapped.find(selector).remove();
    var ret = $.trim(wrapped.html());
    return ret.substring(0, 15);
}

